const { openai } = require('./func/gpt.js');
const { modulPlay } = require('./func/play.js');

async function gpt(query) {
  try {
    const result = await openai(query);
    return result;
  } catch (error) {
    console.error('An error occurred:', error);
    throw error;
  }
}

async function play(query){
  try {
    const result = await modulPlay(query);
    return result
  } catch (error) {
    console.error(error)
  }
}
module.exports = { gpt, play };
  
